mapper.insert:
insert into hm_erp_base (id, base_name, company_id
  )
values (#{id}, #{baseName}, #{companyId}
  )

mapper.update:
update hm_erp_base
set base_name = #{baseName},
  company_id = #{companyId}
where id = #{id}

mapper.select:
select id, base_name, company_id
from hm_erp_base
where id = #{id} and companyId = #{companyId}

select id, base_name, company_id
from hm_erp_base
where company_id = #{companyId}
