mapper.select:
  	SELECT
		ss.id
	FROM
		hm_erp_staff_schedule ss
	WHERE 
	ss.company_id=#{companyId}
	AND ss.staff_id=#{staffId}
	AND ss.venue_id=#{venueId}
	AND ss.staff_day=#{time}

  	SELECT
		ss.id,ss.staff_id,ss.venue_id,sr.role_id,ss.staff_day time
	FROM
	 	hm_erp_process_shoot_schedule shoot
	LEFT JOIN hm_erp_staff_schedule ss ON shoot.shoot_schedule_id=ss.id AND shoot.company_id=ss.company_id
	LEFT JOIN hm_erp_staff_role sr ON sr.staff_id=ss.staff_id AND sr.company_id=ss.company_id
	WHERE
		ss.company_id=#{companyId}
		AND sr.role_id=#{roleId}
		AND ss.venue_id=#{venueId}
		AND ss.staff_status=1
		AND ss.staff_day=#{time}

  	SELECT
		ss.id,ss.staff_id,sr.role_id,ss.venue_id
	FROM
		hm_erp_staff_schedule ss
	LEFT JOIN hm_erp_staff_role sr ON sr.staff_id=ss.staff_id AND sr.company_id=ss.company_id
	WHERE
		ss.company_id=#{companyId}
		AND ss.venue_id=#{venueId}
		AND ss.staff_day=#{time}
		AND sr.role_id=#{roleId}
		AND ss.staff_status=2

  	SELECT
		ss.id,ss.staff_id,sr.role_id,ss.venue_id,FROM_UNIXTIME(ss.staff_day, '%d') time,ss.staff_status
	FROM
		hm_erp_staff_schedule ss
	LEFT JOIN hm_erp_staff_role sr ON sr.staff_id=ss.staff_id AND sr.company_id=ss.company_id
	WHERE
		ss.company_id=#{companyId}

			AND ss.venue_id IN

                #{item}       


		AND ss.staff_day BETWEEN #{firstTime} AND #{endTime}
		AND sr.role_id=#{roleId}
		AND ss.staff_status=2

  	SELECT
		ss.staff_id,ss.staff_day time,ss.staff_status,sr.role_id,ss.venue_id
	FROM
		hm_erp_staff_schedule ss
	LEFT JOIN hm_erp_staff_role sr ON sr.staff_id=ss.staff_id AND sr.company_id=ss.company_id
	WHERE
		ss.company_id=#{companyId}
		AND ss.venue_id=#{venueId}
		AND sr.role_id=#{roleId}
		AND sr.staff_id=#{staffId}
		AND ss.staff_day=#{time}

SELECT v.id,v.venue_name,v.venue_status,v.priority,v.venue_type, sv.staff_id,st.NICKNAME,sv.venue_id,sr.role_level FROM hm_erp_venue v LEFT JOIN hm_erp_staff_venue sv ON sv.venue_id=v.id AND sv.company_id=v.company_id LEFT JOIN hm_pub_staff st ON st.ID=sv.staff_id AND st.COMPANYID=sv.company_id LEFT JOIN hm_erp_staff_role sr ON sr.staff_id=sv.staff_id AND sr.company_id=sv.company_id WHERE v.company_id=#{companyId} AND sr.role_id=#{roleId}
AND v.id IN
#{item}
AND sr.role_level IN
#{item}
AND st.ISDEL=0 AND st.NICKNAME IS NOT NULL GROUP BY v.id,sr.role_level,sr.staff_id ORDER BY sr.role_level

SELECT meal.meal_name,ss.id, ss.venue_id, ss.staff_day_limit, ss.staff_status, ve.venue_name, ss.staff_id,st.NICKNAME, reals.role_level, reals.role_id, r.role_name, FROM_UNIXTIME(ss.staff_day, '%d') time,COUNT(1) count FROM hm_erp_process_shoot_schedule shoot LEFT JOIN hm_erp_staff_schedule ss ON shoot.shoot_schedule_id=ss.id AND shoot.company_id=ss.company_id LEFT JOIN hm_pub_staff st ON st.ID=ss.staff_id AND st.COMPANYID=ss.company_id LEFT JOIN hm_erp_venue ve ON ve.id=ss.venue_id AND ve.company_id=ss.company_id LEFT JOIN hm_erp_staff_role reals ON reals.staff_id =ss.staff_id AND reals.company_id=ss.company_id LEFT JOIN hm_erp_role r ON r.id=reals.role_id AND r.company_id=reals.company_id LEFT JOIN hm_erp_order_process op ON shoot.process_id=op.id AND shoot.company_id=op.company_id LEFT JOIN hm_erp_order eo ON eo.id=op.order_id AND eo.company_id=op.company_id LEFT JOIN hm_erp_meal meal ON eo.meal_id=meal.id AND eo.company_id=meal.company_id WHERE ss.company_id=#{companyId} AND ss.staff_day BETWEEN #{firstTime} AND #{endTime} AND reals.role_id=#{roleId}
AND ve.id IN
#{item}
AND reals.role_level IN
#{item}
GROUP BY time,ss.staff_id
